<a href="#{{$star}}" data-star="{{$star}}" data-rating="{{$rating}}" class="{{$selected ? 'active' : ''}}">
    <span>
        {{$star}}
    </span>
</a>

{{--$star - номер оценки--}}
{{--$rating - обший рейтинг оценки--}}
{{--$data_id - ID рейтинга в общем списке--}}
